mapper.select:
select
id, value, label, type, description, sort, create_by, create_time, last_update_by, last_update_time, remarks,
del_flag
from sys_config


        and id = #{config.id}


        and value = #{config.value}


        and label = #{config.label}


        and type = #{config.type}


        and description = #{config.description}


        and sort = #{config.sort}


        and create_by = #{config.createBy}


        and create_time = #{config.createTime}


        and last_update_by = #{config.lastUpdateBy}


        and last_update_time = #{config.lastUpdateTime}


        and remarks = #{config.remarks}


        and del_flag = #{config.delFlag}

select count(1)
from sys_config


        and id = #{id}


        and value = #{value}


        and label = #{label}


        and type = #{type}


        and description = #{description}


        and sort = #{sort}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}


        and remarks = #{remarks}


        and del_flag = #{delFlag}
