mapper.select:
select
id, name, parent_id, order_num, create_by, create_time, last_update_by, last_update_time, del_flag
from sys_dept


        and id = #{dept.id}


        and name = #{dept.name}


        and parent_id = #{dept.parentId}


        and order_num = #{dept.orderNum}


        and create_by = #{dept.createBy}


        and create_time = #{dept.createTime}


        and last_update_by = #{dept.lastUpdateBy}


        and last_update_time = #{dept.lastUpdateTime}


        and del_flag = #{dept.delFlag}

select count(1)
from sys_dept


        and id = #{id}


        and name = #{name}


        and parent_id = #{parentId}


        and order_num = #{orderNum}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}


        and del_flag = #{delFlag}
