mapper.select:
select
id, user_name, operation, method, params, time, ip, create_by, create_time, last_update_by, last_update_time
from sys_log


        and id = #{log.id}


        and user_name = #{log.userName}


        and operation = #{log.operation}


        and method = #{log.method}


        and params = #{log.params}


        and time = #{log.time}


        and ip = #{log.ip}


        and create_by = #{log.createBy}


        and create_time = #{log.createTime}


        and last_update_by = #{log.lastUpdateBy}


        and last_update_time = #{log.lastUpdateTime}

select count(1)
from sys_log


        and id = #{id}


        and user_name = #{userName}


        and operation = #{operation}


        and method = #{method}


        and params = #{params}


        and time = #{time}


        and ip = #{ip}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}
