mapper.select:
select
id, user_name, status, ip, create_by, create_time, last_update_by, last_update_time
from sys_login_log


        and id = #{loginLog.id}


        and user_name = #{loginLog.userName}


        and status = #{loginLog.status}


        and ip = #{loginLog.ip}


        and create_by = #{loginLog.createBy}


        and create_time = #{loginLog.createTime}


        and last_update_by = #{loginLog.lastUpdateBy}


        and last_update_time = #{loginLog.lastUpdateTime}

select count(1)
from sys_login_log


        and id = #{id}


        and user_name = #{userName}


        and status = #{status}


        and ip = #{ip}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}
