mapper.select:
select
id, name, parent_id, url, perms, type, icon, order_num, create_by, create_time, last_update_by,
last_update_time, del_flag
from sys_menu


        and id = #{menu.id}


        and name = #{menu.name}


        and parent_id = #{menu.parentId}


        and url = #{menu.url}


        and perms = #{menu.perms}


        and type = #{menu.type}


        and icon = #{menu.icon}


        and order_num = #{menu.orderNum}


        and create_by = #{menu.createBy}


        and create_time = #{menu.createTime}


        and last_update_by = #{menu.lastUpdateBy}


        and last_update_time = #{menu.lastUpdateTime}


        and del_flag = #{menu.delFlag}

select count(1)
from sys_menu


        and id = #{id}


        and name = #{name}


        and parent_id = #{parentId}


        and url = #{url}


        and perms = #{perms}


        and type = #{type}


        and icon = #{icon}


        and order_num = #{orderNum}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}


        and del_flag = #{delFlag}
