mapper.select:
select
id, role_id, dept_id, create_by, create_time, last_update_by, last_update_time
from sys_role_dept


        and id = #{roleDept.id}


        and role_id = #{roleDept.roleId}


        and dept_id = #{roleDept.deptId}


        and create_by = #{roleDept.createBy}


        and create_time = #{roleDept.createTime}


        and last_update_by = #{roleDept.lastUpdateBy}


        and last_update_time = #{roleDept.lastUpdateTime}

select count(1)
from sys_role_dept


        and id = #{id}


        and role_id = #{roleId}


        and dept_id = #{deptId}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}
