mapper.select:
select
id, role_id, menu_id, create_by, create_time, last_update_by, last_update_time
from sys_role_menu


        and id = #{roleMenu.id}


        and role_id = #{roleMenu.roleId}


        and menu_id = #{roleMenu.menuId}


        and create_by = #{roleMenu.createBy}


        and create_time = #{roleMenu.createTime}


        and last_update_by = #{roleMenu.lastUpdateBy}


        and last_update_time = #{roleMenu.lastUpdateTime}

select count(1)
from sys_role_menu


        and id = #{id}


        and role_id = #{roleId}


        and menu_id = #{menuId}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and last_update_by = #{lastUpdateBy}


        and last_update_time = #{lastUpdateTime}
